import React, {useState, useEffect} from "react";

function App() {
  const [countries, setCountries] = useState([]);
  const [search, setSearch]=useState('');
  const [filter, setFilter]=useState(false);
  const [tooLong, setTooLong]=useState('')


  useEffect(() => {
    const fetchCountriesData = async () => {
      try {
        const response = await fetch('https://studies.cs.helsinki.fi/restcountries/api/all');
        const data = await response.json();
        setCountries(data);
      } catch (error) {
        console.error('Error fetching address data:', error);
      }
    };

    fetchCountriesData();
  }, []);

  let filteredList=null;
  const handleSearch=(e)=>{
    setSearch(e.target.value);
    setFilter(true);
    filteredList = countries.filter(country => country.name.common && country.name.common.includes(search));
    return filteredList;
  }

    // const filteredList = countries.filter(country => country.name.common && country.name.common.includes(search));
    if (filteredList.length>10){
      <div></div>
    }

  return (
    <div className="App">
    <label htmlFor="search">Find countries</label>
    <input id="search" type="text" name="search" value={search} onChange={handleSearch}></input>
    <ul>
        {filter?
        filteredList.map(country=>
        <li>{country.name.common}</li>)
        :
        countries.map(country=>
        <li>{country.name.common}</li>)
        }
      </ul>    
      </div>
  );
}

export default App;
